import React from 'react'
import { useForm } from "react-hook-form";
import { Register } from '../action/action';
import { useDispatch, useSelector } from 'react-redux';
import Registrationnavbar from '../header/Registrationnavbar';
import { ToastContainer, toast } from 'react-toastify';
import './register.css';
import { Button } from '@mui/material';
const arr = JSON.parse(localStorage.getItem('info'));
console.log(6778,arr);

export default function Login() {
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
 const dispatch = useDispatch();
 const notify = (a) => toast(a);
//  const data = useSelector(state => state.Reducer.dashboard);
  const onSubmit = (data) =>{
    function uuid() {
      return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
      });
    }
    // console.log(data.email);
    //something like: "ec0c22fa-f909-48da-92cb-db17ecdb91c5"
    var b = arr.some((item) => item.email == data.email )
    console.log("arr",b)
    if(b[0] == false)
    {
      var userID=uuid();
      data.token = userID;
      dispatch(Register(data));
      notify("now,you have successfully register")
    }
    else{
      notify("change your Email ID")
    }
    }
  return (
    <div className='bgcolor'>
      <Registrationnavbar/>
          <br/>
          <div style={{border:'2px solid black',width:'38%',borderRadius:'25px',margin:'30px auto 30px auto',padding:'20px',backgroundColor:'rgba(255, 255, 255,0.1)'}} id="resize">
          <form onSubmit={handleSubmit(onSubmit)}>
            <fieldset><legend><h1 style={{color:'red'}}>Registration Form</h1></legend>
          <input  {...register("name",{ required: true })} placeholder="Enter name"/><br/><br/>
      {errors.name && <span style={{color:'red'}}>This field is required</span>}<br/>
      {/* register your input into the hook by invoking the "register" function */}
      <input  {...register("email",{ required: true , pattern:/^[a-zA-Z0-9]+@+[a-zA-Z0-9]+.+[A-z]/})} placeholder="Enter Email"/><br/><br/>
      {errors.email && <span style={{color:'red'}}>This field is required</span>}<br/>
      {/* include validation with required or other standard HTML validation rules */}
      <input {...register("password", { required: true })} placeholder="Enter Password"/><br/><br/>
      {/* errors will return when field validation fails  */}
      {errors.password && <span style={{color:'red'}}>This field is required</span>}<br/>

      <input  {...register("phone",{ required: true })} placeholder="phone"/><br/><br/>
      {errors.phone && <span style={{color:'red'}}>This field is required</span>}<br/>
      <Button variant="contained" color='warning' type="submit">
        Submit
      </Button>

      </fieldset>
    </form>
    </div>
    <ToastContainer />
    </div>
  )
}
